// Function to check if a string contains HTML tags
function containsHTML(str) {
   return /<[^>]*>/.test(str);
}

// Function to clean HTML from a string
function cleanHTML(str) {
   return str.replace(/<[^>]*>/g, "").trim();
}

// Function to check if a string looks like a contract name
function isContractName(str) {
   return /^[A-Za-z]+\d+\s*\([^)]+\)$/.test(str);
}

// Function to check if a string looks like a price/volume/change value
function isPriceValue(str) {
   // Remove commas and check if it's a number or percentage
   const cleanStr = str.replace(/,/g, "");
   return (
      /^[+-]?\d+(\.\d+)?%?$/.test(cleanStr) ||
      /^[+-]?\d+(\.\d+)?$/.test(cleanStr) ||
      /^[A-Za-z]+\d+\s*\([^)]+\)$/.test(str)
   ); // Contract names like "Al2509 (CNY/mt)"
}

// Function to process the prices data into structured objects
function processPrices() {
   try {
      // Read the input file
      const data = $input.all();

      if (!data || !Array.isArray(data) || data.length === 0 || !data[0].json.price) {
         throw new Error("Invalid data structure. Expected array with price property.");
      }

      const rawPrices = data[0].json.price;
      const cleanedPrices = [];
      const structuredPrices = [];

      // First pass: clean all HTML and filter meaningful data
      for (let i = 0; i < rawPrices.length; i++) {
         const item = rawPrices[i];

         if (typeof item === "string") {
            let cleaned = item;
            if (containsHTML(item)) {
               cleaned = cleanHTML(item);
            }

            if (cleaned && cleaned.trim().length > 0) {
               cleanedPrices.push(cleaned.trim());
            }
         }
      }

      // Second pass: organize into structured format
      let currentContract = null;
      let currentPrices = {price:'', open: '', high: '', low: '', volume: '', change: ''};

      for (let i = 0; i < cleanedPrices.length; i++) {
         const item = cleanedPrices[i];

         if (isContractName(item)) {
            // Save previous contract data if exists
            if (currentContract && Object.keys(currentPrices).length > 0) {
               structuredPrices.push({
                  code: currentContract,
                  ...currentPrices,
               });
            }

            // Start new contract
            currentContract = item.substr(0,2);
            currentPrices = {price:'', open: '', high: '', low: '', volume: '', change: ''};
         } else if (isPriceValue(item)) {
            // This is a price value, need to determine what type
            // We'll need to look at the context or position
            if (!currentPrices.price) {
               currentPrices.price = item;
            } else if (!currentPrices.open) {
               currentPrices.open = item;
            } else if (!currentPrices.high) {
               currentPrices.high = item;
            } else if (!currentPrices.low) {
               currentPrices.low = item;
            } else if (!currentPrices.volume) {
               currentPrices.volume = item;
            } else if (!currentPrices.change) {
               currentPrices.change = item;
            }
         }
      }

      // Don't forget the last contract
      if (currentContract && Object.keys(currentPrices).length > 0) {
         structuredPrices.push({
            code: (currentContract.substr(0, 2)),
            ...currentPrices,
         });
      }

      // Return the cleaned data
      return {prices: structuredPrices.slice(0, 7)};

   } catch (error) {
      return {};
   }
}

return processPrices();

